/*
  1.创建数据库
  2.创建表
*/
CREATE DATABASE IF NOT EXISTS `test`;

USE `test`;

DROP TABLE IF EXISTS `category`;
CREATE TABLE `category` (
	`id` int(11) NOT NULL AUTO_INCREMENT,
	`name` varchar(32) NOT NULL COMMENT '类别名称',
	`image` varchar(128) NULL COMMENT '类别图片',
	`desc` varchar(128) NULL COMMENT '类别描述',
	`createdAt` varchar(64) NULL COMMENT '创建时间',
	PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='类别';


DROP TABLE IF EXISTS `serial`;
CREATE TABLE `serial` (
	`id` int(11) NOT NULL AUTO_INCREMENT,
	`name` varchar(32) NOT NULL COMMENT '套书名称',
	`desc` varchar(256) NOT NULL DEFAULT '连环画又称连环图画、连环图、小人书、小书、公仔书等。连环画是一种古老的中国传统艺术，在宋朝印刷术普及后最终成型。' COMMENT '套书描述',
	`cover` varchar(128) NOT NULL COMMENT '套书的封面',
	`createdAt` varchar(64) NOT NULL COMMENT '创建时间',
	`cateId` int(11) NOT NULL COMMENT '类别的id',
	PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='套书';




DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
	`id` int(11) NOT NULL AUTO_INCREMENT,
	`name` varchar(32) NOT NULL COMMENT '画册名称',
	`desc` varchar(256) NULL DEFAULT '',
	`cover` varchar(128) NOT NULL COMMENT '画册的封面',
	`images` text NOT NULL COMMENT '画册的图片地址',
	`from` varchar(64) NOT NULL DEFAULT '' COMMENT '画册的来源',
	`download` varchar(128) NOT NULL DEFAULT '' COMMENT '下载地址',
	`status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '0:未发布 1:已发布',
	`createdAt` varchar(64) NOT NULL COMMENT '创建时间',
	`serialId` int(11) NOT NULL COMMENT '套书系列的id',
	`size` int(11) NULL DEFAULT '0' COMMENT '画册的大小',
	PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8 COMMENT='画册';

